<?phpdefined('_JEXEC') or die; jimport('joomla.form.helper');JFormHelper::loadFieldClass('list'); /*	Класс формирует набор значений параметра меню		Класс связан с конкретным параметром посредством поля $type, которому должен соответствовать	тег type в конфигураторе меню в шаблоне (tmpl/default.xml). Ни имя файла, ни имя класса не играют роли.	Так что можно давать "говорящие" имена.	Если в директории находится только один файл, то и поле $type не играет определяющей роли*/class FieldIDAuroraSearchMenu extends JFormFieldList{	protected $type = 'db_param-bind'; 	/**	 * Method to get a list of options for a list input.	 *	 * @return	array		An array of JHtml options.	 */	protected function getOptions() 	{		$db = JFactory::getDBO();		$query = $db->getQuery(true);		$query->select('db_param,field_1');		$query->from('#__aurora_search_options');		$db->setQuery((string)$query);		$messages = $db->loadObjectList();		$options = array();		if ($messages)		{			foreach($messages as $message) 			{				$options[] = JHtml::_('select.option', $message->db_param, $message->field_1);			}		}		$options = array_merge(parent::getOptions(), $options);		return $options;	}}