DROP TABLE IF EXISTS `#__aurora_search_options`; CREATE TABLE `#__aurora_search_options` (  `db_param` int(11) NOT NULL AUTO_INCREMENT,  `field_1` varchar(25) NOT NULL,  `field_2` varchar(50) NOT NULL,   PRIMARY KEY  (`db_param`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8; INSERT INTO `#__aurora_search_options` (`field_1`, `field_2`) VALUES	('p11', 'p21'),	('p12', 'p22'),	('p13', 'p23');	CREATE TABLE `#__aurora_search_queries` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `kw` varchar(128) NOT NULL,  `amount` int(8) NOT NULL,   PRIMARY KEY  (`id`)) ENGINE=MyISAM AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;